import React, { useEffect, useState } from 'react'
import MainHeading from '../components/MainHeading'
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const API_URL = 'http://localhost:3000'

export default function ProblemSet(token, id) {
  console.log(token);
  console.log(id);

  const [username, setUsername] = useState("");
  const [verified, setVerified] = useState(false);
  const [problemListData, setProblemListData] = useState(null);
  const [searchQ, setSearchQ] = useState("");
  const navigate = useNavigate();

  
  useEffect(() => {
    axios
      .get('http://localhost:3000/api/accounts/id/6558238eb00b42faeb87ef87', {
        headers: {
          Authorization: token,
        }
      })
      .then(({ data }) => {
        setUsername(data.username);
        setVerified(true);
      })
      .catch((error) => {
        console.log(error);
        setVerified(false);
      });
  }, [token]); // Add any dependencies required for the effect to re-run

 

  return (
    <>
      {verified ? (
        <MainHeading data={{ username: username, status: "loggedin" }} />
      ) : (
        <MainHeading data={{ status: "none" }} />
      )};

    </>
  );
};
