import React, { useEffect, useState } from 'react'
import MainHeading from '../components/MainHeading'
import { useNavigate } from 'react-router-dom';
import axios from 'axios';


export default function ProblemSet(props) {

  const [username, setUsername] = useState("");
  const [verified, setVerified] = useState(false);




  useEffect(() => {
    axios
      .get(`http://localhost:3000/api/accounts/id/${props.id}`, {
        headers: {
          Authorization: props.token,
        }
      })
      .then(({ data }) => {
        setUsername(data.username);
        setVerified(true);
        console.log('connected');
      })
      .catch((error) => {
        console.log(error);
        setVerified(false);
      });

  }, []);



  return (
    <>
      {verified ? (
        <MainHeading data={{ username: username, status: "loggedin" }} />
      ) : (
        <MainHeading data={{ status: "none" }} />
      )};

    <div className="h-[calc(100vh-60px)] overflow-hidden bg-black">
                <div id="cont" className="relative flex flex-row h-[calc(100vh-60px)] w-full mt-[8px] ">
                    <div id="explanation" className="h-[calc(100%-16px)] bg-black border border-borders ml-[8px] rounded-lg w-[calc(100%-16px)] overflow-hidden" >
                        <div className="w-full bg-black border-b border-borders ">
                            <div className="ml-[9px]">
                                {/* <CustomNavbar data={customNavData} /> */}
                            </div>
                        </div>
                        <div className="w-full bg-black h-[40px] relative border-b border-borders">
                            <input
                                type="text"
                                placeholder="Search questions..."
                                // onChange={(e) => {
                                //     handleSearch(e.target.value);
                                //     setSearchQ(e.target.value);
                                // }}
                                className="bg-black outline-none border-none relative -translate-y-1/2 top-1/2 left-[9px] px-[20px] text-[14px] h-[calc(100%-2px)] placeholder:text-[14px] placeholder:text-text_2 w-[calc(100%-100px)]"
                            />
                        </div>
                    </div>
                </div>
            </div>
        </>
    );
};
