import React, { useEffect, useState } from 'react'
import MainHeading from '../components/MainHeading'
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import CustomNavbar from '../components/CustomNavbar';


export default function ProblemSet(props) {

  const [username, setUsername] = useState("");
  const [verified, setVerified] = useState(false);




  useEffect(() => {
    axios
      .get(`http://localhost:3000/api/accounts/id/${props.id}`, {
        headers: {
          Authorization: props.token,
        }
      })
      .then(({ data }) => {
        setUsername(data.username);
        setVerified(true);
        console.log('connected');
      })
      .catch((error) => {
        console.log(error);
        setVerified(false);
      });

  }, []);



  return (
    <>
      

      <div className='absolute'>
      {verified ? (
          <MainHeading data={{ username: username, status: "loggedin" }} />
        ) : (
          <MainHeading data={{ status: "none" }} />
        )};
        <div class="h-[calc(100vh-60px)] overflow-hidden bg-black">
          <div id="cont" class="relative flex flex-row h-[calc(100vh-60px)] w-full mt-[8px] ">
            <div id="explanation" class="h-[calc(100%-16px)] bg-black border border-borders ml-[8px] rounded-lg w-[calc(100%-16px)] overflow-hidden">
              <div class="w-full bg-black border-b border-borders ">
                <div class="ml-[9px]">
                  <div style="background-color: rgb(0, 0, 0); color: rgb(128, 128, 128); width: fit-content; 
                  position: relative; display: flex; flex-direction: row; height: 50px; font-size: 14px; 
                  align-items: center; overflow: hidden; user-select: none;">
                    <div class="h-[32px] absolute opacity-transition box-content rounded" id="hover-rect" 
                    style="transition: transform 150ms ease 0s, width 150ms ease 0s, opacity 150ms ease 0s; 
                    transform: translateX(214px); opacity: 0; background-color: rgb(51, 51, 51); width: 110px;">
                    </div>
                    <div id="active-underline" class="absolute h-[2px] bottom-0" style="transition: 
                    all 150ms ease 0s; transform: translateX(20px); background-color: rgb(255, 255, 255); 
                    width: 63px;">
                    </div>
                      <a href="/problemset">
                        <div id="all-topics" class="hover-color active-color" style="position: relative; 
                        z-index: 40; padding: 14px 20px; cursor: pointer; transition: all 150ms ease 0s;">All Topics</div>
                      </a>
                      <a href="/problemset">
                        <div id="algorithms" class="hover-color " style="position: relative; z-index: 40; 
                        padding: 14px 20px; cursor: pointer; transition: all 150ms ease 0s;">Algorithms</div>
                      </a>
                      <a href="/problemset">
                        <div id="javascript" class="hover-color " style="position: relative; 
                        z-index: 40; padding: 14px 20px; cursor: pointer; transition: all 150ms ease 0s;">JavaScript</div>
                      </a>
                      <a href="/problemset">
                        <div id="database" class="hover-color " style="position: relative; z-index: 40; 
                          padding: 14px 20px; cursor: pointer; transition: all 150ms ease 0s;">DataBase</div>
                      </a>
                      <a href="/problemset">
                        <div id="shell" class="hover-color " style="position: relative; z-index: 40; 
                        padding: 14px 20px; cursor: pointer; transition: all 150ms ease 0s;">Shell</div>
                      </a>
                      {/* <style>
                        .hover-color:hover { }
                        .active-color{}
                      </style> */}
                    </div>
                  </div>
                </div>
              <div class="w-full bg-black h-[40px] relative border-b border-borders">
                <input type="text" placeholder="Search questions..." class="bg-black outline-none border-none relative -translate-y-1/2 top-1/2 left-[9px] px-[20px] text-[14px] h-[calc(100%-2px)] placeholder:text-[14px] placeholder:text-text_2 w-[calc(100%-100px)]" />
              </div>
            </div>
          </div>
        </div>
      </div>
  </>
  );
};
