import React, { useEffect, useState } from 'react'
import MainHeading from '../components/MainHeading'
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const API_URL = 'http://localhost:3000'

export default function ProblemSet(props) {

  const [username, setUsername] = useState("");
  const [verified, setVerified] = useState(false);



  useEffect(() => {
    axios
      .get(`http://localhost:3000/api/accounts/id/${props.id}`, {
        headers: {
          Authorization: props.token,
        }
      })
      .then(({ data }) => {
        setUsername(data.username);
        setVerified(true);
        console.log('connected');
      })
      .catch((error) => {
        console.log(error);
        setVerified(false);
      });

    axios
      .post(`http://localhost:3000/api/problem/all`, { id: props.id })
      .then(({ data }) => {
        //setProblemListData(data);
        console.log('data fetched');
      })
      .catch((e)=>{
        console.log(e);
      });
  }, []);



  return (
    <>
      {verified ? (
        <MainHeading data={{ username: username, status: "loggedin" }} />
      ) : (
        <MainHeading data={{ status: "none" }} />
      )};

    </>
  );
};
