import React, { useEffect, useState } from 'react'
import MainHeading from '../components/MainHeading'
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const API_URL = 'http://localhost:3000'

export default function ProblemSet(token, id) {
  console.log(token);
  console.log(id);

  const [username, setUsername] = useState("");
  const [verified, setVerified] = useState(false);
  const [problemListData, setProblemListData] = useState(null);
  const [searchQ, setSearchQ] = useState("");
  const navigate = useNavigate();

  const handleSearch = async (
    searchQuery,
    options = {
        acceptance_rate_count: "",
        difficulty: "",
        title: "",
    }
) => {
    const { acceptance_rate_count, difficulty, title } = options;
    try {
        const { data } = await axios.post(
            `${API_URL}/api/problem/all?search=${searchQuery}&acceptance=${acceptance_rate_count}&difficulty=${difficulty}&title=${title}`,
            { id }
        );
        setProblemListData(data);
    } catch (error) {
        console.error("Error searching:", error);
    }
};

  useEffect(() => {
    axios
      .get(`http://localhost:3000/api/accounts/id/${id}`, {
        headers: {
          Authorization: token,
        }
      })
      .then(({ data }) => {
        setUsername(data.username);
        setVerified(true);
      })
      .catch((e) => {
        console.log(e);
        setVerified(false);
      });

    axios
      .post(`http://localhost:3000/api/problem/all`, { id: id })
      .then(({ data }) => {
        setProblemListData(data);
      });
  }, []);





  return (
    <>
      {verified ? (
        <MainHeading data={{ username: username, status: "loggedin" }} />
      ) : (
        <MainHeading data={{ status: "none" }} />
      )};

      <div className="h-[calc(100vh-60px)] overflow-hidden bg-black">
        <div id="cont" className="relative flex flex-row h-[calc(100vh-60px)] w-full mt-[8px] ">
          <div id="explanation" className="h-[calc(100%-16px)] bg-black border border-borders ml-[8px] rounded-lg w-[calc(100%-16px)] overflow-hidden">
            <div className="w-full bg-black border-b border-borders ">
              <div className="ml-[9px]">

              </div>
            </div>
            <div className="w-full bg-black h-[40px] relative border-b border-borders">
              <input
                type="text"
                placeholder="Search questions..."
                onChange={(e) => {
                  handleSearch(e.target.value);
                  setSearchQ(e.target.value);
                }}
                className="bg-black outline-none border-none relative -translate-y-1/2 top-1/2 left-[9px] px-[20px] text-[14px] h-[calc(100%-2px)] placeholder:text-[14px] placeholder:text-text_2 w-[calc(100%-100px)]"
              />
            </div>
            <div>

            </div>
          </div>
        </div>
      </div>
    </>
  );
};
